mapper.select:
SELECT es.equipmentNum, s.statName, es.statType, es.value FROM EquipmentStat es, Stat s WHERE equipmentNum IN
(#{equipmentNum})
AND es.statCode = s.statCode;

SELECT statName, statType, value
FROM EquipmentStat es, Stat s
WHERE es.equipmentNum = #{equipmentNum} AND es.statCode = s.statCode;
