mapper.select:
SELECT n.*,
       CASE WHEN f.targetId IS NULL THEN 0 ELSE 1 END AS isFavorite
FROM novel n
         LEFT JOIN favorite f
                   ON f.targetType = 'Novel'
                       AND f.uuid = #{uuid}
                       AND f.targetId = n.novelNum
WHERE n.uuid = #{uuid}
ORDER BY isFavorite DESC;

SELECT IFNULL((
    SELECT 1
    FROM Novel n
    WHERE n.novelNum = #{novelNum} AND n.uuid = #{uuid}
    LIMIT 1
           ), 0)
