mapper.select:
SELECT p.postId, p.postTitle, u.userName
FROM post p
         JOIN user u ON p.uuid = u.uuid
WHERE p.boardId = #{boardId}
ORDER BY p.postId DESC
LIMIT 20 OFFSET #{page};

SELECT p.postId, p.postTitle, u.userName
FROM post p
         JOIN user u ON p.uuid = u.uuid
WHERE p.boardId = #{boardId} AND p.uuid = #{uuid}
ORDER BY p.postId DESC
LIMIT 20 OFFSET #{page};
